package com.syntax.class3;

public class EvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number=10;
		int rem=number%2;
		if(rem==0)
		{
        System.out.println("The given number is even");
	    }
		else
		{
			System.out.println("The given number is odd");
		}
}

}
